/* 归并排序 End */
/* 普通分类排序 Start */
function normalSort(arr) {
    var len = arr.length;
    if (len < 2) {
        return arr;
    }
    let upResult = [];
    let otherResult = [];
    while(arr.length) {
        let item = arr.shift();
        if(item.title.includes("🆙")) {
            upResult.push(item)
        } else {
            otherResult.push(item)
        }
    }
    return upResult.concat(otherResult);
}
