                let headers={};
                if(it.split(";").length>1){
                    let head = it.split(';')[1];
                    if(/@/.test(head)&&/{|}/.test(head)){
//head = head.replace(/{|}/g,'');
                        head = head.split("{")[1].split("}")[0];
                        head = head.split('&&');
                        for(let i in head){
                            let key = head[i].split('@')[0];
