}

function isMovie(it) {
    if (it == null || it.text == null) {
        return false;
    }
    let tit = it.title || "";
    it = it.text || "";
    if (it == "" || it.length > 8) {
        return false;
    }
    //排除
    let reg = /\.|高清直播|写真推荐|影院|影视|蓝光电影|高清电影|第一季|第二季|第三季|第四季|第五季/;
    if (tit != "" && !tit.includes(it) || reg.test(it)) {
        return false;
    }
    return it.match(/原画|备用|蓝光|超清|高清|正片|韩版|4K|4k|1080P|720P|TC|HD|BD/)
}

function notChapter(it) {
    if (it == null || it.text == null) {
        return true;
    }
    return it.text.match(/[0-9]\.[0-9]分/);
}

function isChapter(it, pre, next) {
    if (notChapter(it)) {
        //优先排除
        return false;
    }
    //判断是不是电影
