        return true;
    }
    return isChapter0(it, pre) || isChapter0(it, next);
}

function getChapterNum(it) {
    if (it == null || it.text == null) {
        return -1;
    }
    it = it.text || "";
    if (it == "") {
        return -1;
    }
    it = clearText(it);
    let reg = /^[0-9]*$/;
    if (!reg.test(it)) {
        return -1;
    }
    it = parseInt(it);
    if (isNaN(it)) {
        return -1;
    }
    if (it > 1900 && it < 2100) {
        return -1;
    }
    return it;
}

function isChapter0(it, brother) {
    /*if (debug) {
        log({
            it: it,
            brother: brother
        });
    }*/
    it = getChapterNum(it);
    //if (debug) log(it);
    if (it < 0) {
        return false;
    }
    brother = getChapterNum(brother);
    //if (debug) log(brother);
    if (brother < 0) {
        return false;
    }
    return it - brother < 2 && it - brother > -2;
}

