"},{"col_type":"movie_3","name":"公共函数","path":"functions","rule":"function JSONRecur(json, path, value) {
    if (path.length == 1) {
        if (value === undefined) {
            return json[path[0]]
        } else if (typeof value == 'function') {
            json[path[0]] = value(json[path[0]])
        } else json[path[0]] = value
    } else {
        return JSONRecur(json[path[0]], path.slice(1), value)
    }
}

function typeConvert(value, type) {
    let vType = Object.prototype.toString.call(value).slice(8, -1)
    switch (type) {
        case 'Object':
            switch (vType) {
                case 'Object':
                    return value
                case 'Array':
                    let obj = {}
                    for (let v of value) {
                        if (v && Object.keys(v).length == 2
                            && 'key' in v && 'value' in v &&
                            !Object.hasOwn(obj, v.key)) {
                            obj[v.key] = v.value
                        } else break
                    }
                    if (Object.keys(obj).length < value.length) {
                        obj = Object.assign({}, value)
                        for (let k in obj) if (obj[k] === null) delete obj[k]
                    }
                    return obj
                case 'String':
                    try {
                        return typeConvert(JSON.parse(value), type)
                    } catch (e) { return {} };
                default:
                    return {}
            };
        case 'Array':
            switch (vType) {
                case 'Object':
                    let keys = Object.keys(value), length = 0
                    for (let k of keys)
                        if (/^\d+$/.test(k)) {
                            k = Number(k) + 1
                            if (length < k) length = k
                        } else {
                            length = 0
                            break
                        }
                    return length > 0
                        ? Array.from(Object.assign(value, { length: length }))
                        : keys.map((k) => ({ key: k, value: value[k] }))
                case 'Array':
                    return value
                case 'String':
                    try {
                        return typeConvert(JSON.parse(value), type)
                    } catch (e) { return [] };
                default:
                    return []
            };
        case 'String':
            switch (vType) {
                case 'Object':
                case 'Array':
                    return JSON.stringify(value)
                default:
                    return String(value)
            };
        case 'Number':
            return Number(value) || 0
        case 'Boolean':
            try {
                while (typeof value == 'string')
                    value = JSON.parse(value)
            } catch (e) {}
            return Boolean(value)
        case 'Null':
            return null
    }
}

function objItemSel(input, k) {
    switch (input.slice(3)) {
        case '展开键值':
            return 'hiker://page/content#noRefresh##noHistory##noRecordHistory#'
        case '更改键名':
            return $(k, '更改的键名\n不能与对象中已有键名重复').input((k, Path, Hash) => {
                eval(JSON.parse(request('hiker://page/functions')).rule)
                let AllJson = JSON.parse(readFile('JSON_' + Hash, 0) || '{}'),
                    Json = JSONRecur(AllJson, Path)
                if (Object.hasOwn(Json, input))
                    return `toast://无法更改，对象中已有[${input}]键`
                JSONRecur(AllJson, Path, (self) => (self[input] = self[k], delete self[k], self))
                saveFile('JSON_' + Hash, JSON.stringify(AllJson), 0)
                refreshPage(false)
            }, k, Path, Hash)
        case '删除项目':
            return $(`确定要删除键名[${k}]的项目吗？`).confirm((k, Path, Hash) => {
                eval(JSON.parse(request('hiker://page/functions')).rule)
                let AllJson = JSON.parse(readFile('JSON_' + Hash, 0) || '{}')
                JSONRecur(AllJson, Path, (self) => (delete self[k], self))
                saveFile('JSON_' + Hash, JSON.stringify(AllJson), 0)
                refreshPage(false)
                return 'hiker://empty'
            }, k, Path, Hash)
        case '复制键名':
            return 'copy://' + k
        case '复制键值':
            return $('#noLoading#').lazyRule((Path, Hash) => {
                eval(JSON.parse(request('hiker://page/functions')).rule)
                let AllJson = JSON.parse(readFile('JSON_' + Hash, 0) || '{}')
                return 'copy://' + typeConvert(JSONRecur(AllJson, Path), 'String')
            }, Path.concat(k), Hash)
    }
}

function arrItemSel(input, i) {
    switch (input.slice(3)) {
        case '展开内容':
            return 'hiker://page/content#noRefresh##noHistory##noRecordHistory#'
        case '移动位置':
            return $(String(i), '要移动到的位置序号\n置空则移至末尾').input((i, Path, Hash) => {
                if (input === '') input = Infinity
                else {
                    input = parseInt(input, 10)
                    if (isNaN(input)) return 'toast://请正确输入数字序号'
				}
                eval(JSON.parse(request('hiker://page/functions')).rule)
                let AllJson = JSON.parse(readFile('JSON_' + Hash, 0) || '{}')
                JSONRecur(AllJson, Path, (self) => (self.splice(input, 0, self.splice(i, 1)[0]), self))
                saveFile('JSON_' + Hash, JSON.stringify(AllJson), 0)
                refreshPage(false)
            }, i, Path, Hash)
        case '删除项目':
            return $(`确定要删除序号[${i}]的内容吗？`).confirm((index, Path, Hash) => {
                eval(JSON.parse(request('hiker://page/functions')).rule)
                let AllJson = JSON.parse(readFile('JSON_' + Hash, 0) || '{}')
                JSONRecur(AllJson, Path, (self) => (self.splice(index, 1), self))
                saveFile('JSON_' + Hash, JSON.stringify(AllJson), 0)
                refreshPage(false)
                return 'hiker://empty'
            }, i, Path, Hash)
        case '复制内容':
            return $('#noLoading#').lazyRule((Path, Hash) => {
                eval(JSON.parse(request('hiker://page/functions')).rule)
                let AllJson = JSON.parse(readFile('JSON_' + Hash, 0) || '{}')
                return 'copy://' + typeConvert(JSONRecur(AllJson, Path), 'String')
            }, Path.concat(i), Hash)
    }
}

function setSKey(searchKey) {
    let search = JSON.parse(getMyVar('search_' + Hash, '{}'))
    search[Path] = searchKey
    putMyVar('search_' + Hash, JSON.stringify(search))
    refreshPage(false)
}

function objSearchParse(searchKey, k, vStr, vType) {
    return searchKey.split('|').some((OR) => {
        return OR.split('&').every((AND) => {
            let [mode, cont] = AND.split('@')
            switch (mode) {
                case 'key':
                    return k.includes(cont)
                case 'value':
                    return vStr.includes(cont)
                case 'type':
                    return vType == cont
            }
        })
    })
}
function arrSearchParse(searchKey, i, vLabel, vStr, vType, length) {
    return searchKey.split('|').some((OR) => {
        return OR.split('&').every((AND) => {
            let [mode, cont] = AND.split('@')
            switch (mode) {
                case 'range':
                    let [st, ed] = cont.split('~')
                        .map((v) => {
                            v = parseInt(v)
                            if (v < 0) v += length
                            return v
                        })
                    return st <= i && i <= ed
                case 'label':
                    return vLabel.includes(cont)
                case 'value':
                    return vStr.includes(cont)
                case 'type':
                    return vType == cont
            }
        })
    })
}
