var d = [];
var html = getResCode();

var lazy = $('').lazyRule(() => {

    function toUrl(playUrl) {
        if (/mgtv|sohu/.test(playUrl)) {
            return playUrl + ";{User-Agent@Mozilla/5.0 (Windows NT 10.0)}";
        } else if (/bili/.test(playUrl)) {
            return playUrl + ";{User-Agent@Mozilla/5.0&&Referer@https://www.bilibili.com}";
        } else if (/ixigua/.test(playUrl)) {
            return playUrl + "#isVideo=true#" + "#.mp4;{Referer@https://www.ixigua.com/&&User-Agent@Mozilla/5.0}";
        } else {
            return playUrl;
        }
    }

    var playUrl = "";
    try {
        var html = request(input, {});
        var player_data = JSON.parse(html.match(/r player_.*?=(.*?)</)[1]);

        var fro = player_data.from;
        playUrl = player_data.url;

        if (player_data.encrypt == '1') {
            playUrl = unescape(player_data.url);
        } else if (player_data.encrypt == '2') {
            playUrl = unescape(base64Decode(player_data.url));
        };
        //如果播放地址存在m3u8等扩展名直接返回
        var exclude = /playm3u8|m3u8\.tv|min\.css|404\.m3u8|\.css/;
        var contain = /\.mp4|\.m3u8|\.flv|\.avi|\.mpeg|\.wmv|\.mov|\.rmvb|\.dat|qqBFdownload|mime=video%2F|video_mp4/;
        if (!exclude.test(playUrl) && contain.test(playUrl)) {
            return toUrl(playUrl);
        }

        //获取下网站配置文件的解析接口
        var jvs = parseDom(html, ".player-box-main&&script,1&&src");
        eval(request(jvs, {}));
        var jiek = '';

        if (MacPlayerConfig.player_list[fro].ps == "1") {
            jiek = MacPlayerConfig.player_list[fro].parse == '' ? MacPlayerConfig.parse : MacPlayerConfig.player_list[fro].parse;
        } else {
            var jxkJSPath = "https://vipmv.tv/static/player/" + fro + ".js";
            var jiekHtml = request(jxkJSPath, {});
            //log(jiekHtml)
            if (jiekHtml == '') {
                return 'toast://该线路解析文件无法访问'
            }
            jiek = jiekHtml.match(/src="(.*?)'/)[1];
        }
        if (jiek != '') {
            if (jiek.substring(0, 4) == 'http') {
                jiek = jiek;
            } else if (jiek.substring(0, 2) == '\/\/') {
                jiek = 'https:' + jiek;
            } else {
                jiek = "https://vipmv.tv" + jiek
            }
        }

        var jiexi = jiek + playUrl + '&tm=' + player_data.tm + '&key=' + player_data.key + '&next=' + player_data.link_next + '&title=' + pdfh(html, "title&&Text").split("-")[0];
        //log("jiexi-->" + jiexi);

        //请求配置文件里的解析接口并返回播放地址
        var jxkHtml = request(jiexi, {});
        //log(jxkHtml);
        if (jxkHtml.indexOf('解析失败') != -1) {
            return 'toast://解析失败，请稍后再试。'
        } else if (jxkHtml.indexOf('502 Bad') != -1) {
            return 'toast://502该线路解析已失效'
        } else {
            var apiUrl = jiexi.split('?')[0] + 'API.php';
            eval(jxkHtml.match(/var config = {[\s\S]*?}/)[0] + "");
            if (!exclude.test(config.url) && contain.test(config.url)) {
				return toUrl(config.url);
			}
            var apiHtml = request(apiUrl, {
                headers: {
                    "User-Agent": "Mozilla/5.0 (Windows NT 10.0)"
                },
                body: "url=" + config.url + "&time=" + config.time + "&key=" + config.key,
                method: "POST"
            });
            var json = JSON.parse(apiHtml);
            if (json.code == 200) {
                playUrl = json.url;
                return toUrl(playUrl);
            } else {
                return 'toast://' + json.msg;
            }
        }
    } catch (e) {
        //setError(e);
        return 'toast://嗅探异常，可能链接无法访问。'
    }
});

