var lazy = $('').lazyRule(() => {
    function toUrl(playUrl) {
        if (/mgtv|sohu/.test(playUrl)) {
            return playUrl + ";{User-Agent@Mozilla/5.0 (Windows NT 10.0)}";
        } else if (/bili/.test(playUrl)) {
            return playUrl + ";{User-Agent@Mozilla/5.0&&Referer@https://www.bilibili.com}";
        } else if (/ixigua/.test(playUrl)) {
            return playUrl + "#isVideo=true#" + "#.mp4;{Referer@https://www.ixigua.com/&&User-Agent@Mozilla/5.0}";
        } else {
            return playUrl;
        }
    }

    var playUrl = "";
    try {
        var html = request(input, {});
        var player_data = JSON.parse(html.match(/r player_.*?=(.*?)</)[1]);

        var fro = player_data.from;
        var thumb = player_data.vod_pic_thumb;
        var title = player_data.vod_title + '+' + player_data.vod_title_name;
        var id = player_data.id;
        var nid = player_data.nid;

        playUrl = player_data.url;

        if (player_data.encrypt == '1') {
            playUrl = unescape(player_data.url);
        } else if (player_data.encrypt == '2') {
            playUrl = unescape(base64Decode(player_data.url));
        };

        //如果播放地址存在m3u8等扩展名直接返回
        var exclude = /playm3u8|m3u8\.tv|min\.css|404\.m3u8|\.css/;
        var contain = /\.mp4|\.m3u8|\.flv|\.avi|\.mpeg|\.wmv|\.mov|\.rmvb|\.dat|qqBFdownload|mime=video%2F|video_mp4/;
        if (!exclude.test(playUrl) && contain.test(playUrl)) {
            return toUrl(playUrl);
        }

        //获取下网站配置文件的解析接口
        var jvs = parseDom(html, "body&&.embed-responsive&&script,1&&src");
        eval(request(jvs, {}));
        var jiek = '';

        if (MacPlayerConfig.player_list[fro].ps == "1") {
            jiek = MacPlayerConfig.player_list[fro].parse == '' ? MacPlayerConfig.parse : MacPlayerConfig.player_list[fro].parse;
        } else {
            var jxkJSPath = "https://www.ysgc.vip/static/player/" + fro + ".js";
            var jiekHtml = request(jxkJSPath, {});
            jiek = jiekHtml.match(/src="(.*?)'/)[1];
        }
        if (jiek != '') {
            if (jiek.substring(0, 4) == 'http') {
                jiek = jiek;
            } else if (jiek.substring(0, 2) == '\/\/') {
                jiek = 'https:' + jiek;
            } else {
                jiek = "https://www.ysgc.vip" + jiek
            }
        }
        var jiexi = jiek + playUrl + "&jump=" + input + "&title=" + title + "&thumb=" + thumb + "&id=" + id + "&nid=" + nid;
        //log("jiexi-->" + jiexi);
        var playerHtml = request(jiexi, {
            headers: {
                "User-Agent": MOBILE_UA,
                "Referer": input
            }
        });
        require("https://vkceyugu.cdn.bspapp.com/VKCEYUGU-03ee1f89-f0d4-49aa-a2b3-50e203514d8a/2e54cc42-9b4c-457d-b2de-0cdf3e2aeeaa.js");
        playUrl = getVideoInfo(playerHtml.match(/var urls = \'(.*?)\'/)[1]);
       log(playUrl)
        return toUrl(playUrl);

    } catch (e) {
        showLoading("嗅探失败，通免检索中请稍候...");
        var video = 'x5Rule://' + input + '@' + $.toString(() => {
            //fba.log(fba.getUrls());
            var urls = _getUrls();
            if (urls.length > 0) {
                var exclude = /playm3u8|m3u8\.tv|min\.css|404\.m3u8|\.css/;
                var contain = /\.mp4|\.m3u8|\.flv|\.avi|\.mpeg|\.wmv|\.mov|\.rmvb|\.dat|qqBFdownload|mime=video%2F|video_mp4/;
                for (var i in urls) {
                    if (!exclude.test(urls[i]) && contain.test(urls[i])) {
                        //fy_bridge_app.log(urls[i])
                        if (/mgtv|sohu/.test(urls[i])) {
                            return urls[i] + ";{User-Agent@Mozilla/5.0 (Windows NT 10.0)}";
                        } else if (/bili/.test(urls[i])) {
                            return urls[i] + ";{User-Agent@Mozilla/5.0&&Referer@https://www.bilibili.com}";
                        } else {
                            return urls[i]
                        }
                    }
                }
            } else {
                fba.hideLoading();
                return 'toast://嗅探失败，可能链接无法访问。'
            }
        });
        return video;
        //setError(e);
        //return 'toast://嗅探异常，可能链接无法访问。'
    }
});

