js:
var d = [];
var html = getResCode();

//素白白免嗅by家娃
var lazy = $("").lazyRule(() => {
    eval(getCryptoJS());

    var playerHtml = request(input);
    var dStr = playerHtml.match(/var d(.*?)"/)[1];    
    dStr = "d" + dStr.substring(0, dStr.length - 1);
    //log(dStr);
    var kStr = playerHtml.match(/var k(.*?)"/)[1];
    //log(kStr);
    kStr = "k" + kStr.substring(0, kStr.length - 1);
    var html2 = playerHtml.replace(dStr, '你大爷').replace(kStr, '你大妈');
    //log(html2);
    var mydata = html2.match(/你大爷="(.*?)"/)[1];
    var myKey = html2.match(/var 你大妈\("(.*?)"/)[1];
	var myIv = html2.match(/var iv=md5.enc.Utf8.parse\((.*?)\)/)[1];
    //log(mydata);
    //log(myKey+"*********"+myIv);
    myKey = CryptoJS.enc.Utf8.parse(myKey);
    var iv = CryptoJS.enc.Utf8.parse(myIv); //1234567890983456
    var decrypted = CryptoJS.AES.decrypt(mydata, myKey, {
        iv: iv,
        mode: CryptoJS.mode.CBC,
        padding: CryptoJS.pad.Pkcs7
    });
    var funstr = decrypted.toString(CryptoJS.enc.Utf8);
    funstr = funstr.replace(/'/g, '"'); 
    //log(funstr);
    var jsurl = funstr.match(/url: "(.*?)"/)[1];
   // log(jsurl);
    if (jsurl.search(/m3u8|mp4/) > -1) {
        var video = jsurl;
    } else {
        var play = input;
        showLoading("网页嗅探中,请稍后...");
        var video = 'x5Rule://' + play + '@' + $.toString(() => {
            var urls = _getUrls()
            for (var i in urls) {
                if (!urls[i].includes("url=") && urls[i].match(/\.mp4|\.m3u8|video/)) {
                    //fy_bridge_app.log(urls[i])
