var d = [];
var html = getResCode();

var lazy = $('').lazyRule(() => {
    function toUrl(playUrl) {
        if (/mgtv|sohu/.test(playUrl)) {
            return playUrl + ";{User-Agent@Mozilla/5.0 (Windows NT 10.0)}";
        } else if (/bili/.test(playUrl)) {
            return playUrl + ";{User-Agent@Mozilla/5.0&&Referer@https://www.bilibili.com}";
        } else if (/ixigua/.test(playUrl)) {
            return playUrl + "#isVideo=true#" + "#.mp4;{Referer@https://www.ixigua.com/&&User-Agent@Mozilla/5.0}";
        } else {
            return playUrl;
        }
    }

    function Sniffing(playUrl) {
        showLoading("正在进行检索，请稍候...");
        var video = 'x5Rule://' + playUrl + '@' + $.toString(() => {
            //fba.log(fba.getUrls())
            var urls = _getUrls()
            var exclude = /playm3u8|m3u8\.tv|min\.css|404\.m3u8|\.css|\.jpg|\.jpeg|\.png|\.gif/;
            var contain = /\.mp4|\.m3u8|\.flv|\.avi|\.mpeg|\.wmv|\.mov|\.rmvb|\.dat|qqBFdownload|mime=video%2F|video_mp4/;
            for (var i in urls) {
                if (!exclude.test(urls[i]) && contain.test(urls[i])) {
                    fba.hideLoading();
                    fy_bridge_app.log(urls[i])
                    if (/mgtv|sohu/.test(urls[i])) {
                        return urls[i] + ";{User-Agent@Mozilla/5.0 (Windows NT 10.0)}";
                    } else if (/bili/.test(urls[i])) {
                        return urls[i] + ";{User-Agent@Mozilla/5.0&&Referer@https://www.bilibili.com}";
                    } else if (/ixigua/.test(urls[i])) {
                        return urls[i] + "#isVideo=true#" + "#.mp4;{Referer@https://www.ixigua.com/&&User-Agent@Mozilla/5.0}";
                    } else {
                        return urls[i]
                    }
                }
            }
        });
        return video;
    }

    var playUrl = "";
    try {
        var html = request(input, {});
        eval(pdfh(html, '.player-wrapper&&script&&Html'))

        var fro = player_aaaa.from;
        var next = player_aaaa.link_next;
        var tittle = pdfh(html, "title&&Text").split("-")[0];
        playUrl = player_aaaa.url;

        if (player_aaaa.encrypt == '1') {
            playUrl = unescape(player_aaaa.url);
        } else if (player_aaaa.encrypt == '2') {
            playUrl = unescape(base64Decode(player_aaaa.url));
        };
        var exclude = /playm3u8|m3u8\.tv|min\.css|404\.m3u8|\.css|\.jpg|\.jpeg|\.png|\.gif/;
        var contain = /\.mp4|\.m3u8|\.flv|\.avi|\.mpeg|\.wmv|\.mov|\.rmvb|\.dat|qqBFdownload|mime=video%2F|video_mp4/;
        if (!exclude.test(playUrl) && contain.test(playUrl)) {
            return toUrl(playUrl);
        }
        //获取下网站配置文件的解析接口
        var jvs = parseDom(html, "body&&.player-wrapper&&script,1&&src");
        eval(request(jvs, {}));
        var jiek = '';
        if (MacPlayerConfig.player_list[fro].ps == "1") {
            jiek = MacPlayerConfig.player_list[fro].parse == '' ? MacPlayerConfig.parse : MacPlayerConfig.player_list[fro].parse;
        }
        // log(jiek)
        if (jiek != '') {
            var jiexi = jiek + playUrl;
            //log("jiexi-->" + jiexi);
            var playerHtml = request(jiexi, {
                headers: {
                    "User-Agent": MOBILE_UA
                }
            });
            //log(playerHtml);
            if (playerHtml.indexOf('iframe') > 0) {
                return Sniffing(jiexi);
            } else {
                eval(playerHtml.match(/var config = {[\s\S]*?}/)[0] + "");
                if (fro == 'Good') {
                    var apiUrl = jiexi.split('?')[0] + 'API.php';
                    var jsonStr = request(apiUrl, {
                        headers: {
                            "User-Agent": MOBILE_UA
                        },
                        body: "url=" + config.url + "&time=" + config.time + "&key=" + config.key,
                        method: "POST"
                    });
                    if (jsonStr.indexOf('解析失败') > 0) {
                        return 'toast://解析失败，请重试或切换线路~'
                    }
                    var json = JSON.parse(jsonStr);
                    if (json.code == 200) {
						return toUrl(json.url);
                    } else {
                        return 'toast://解析失败。'
                    }
                } else {
                    playUrl = config.url;
                    if (playUrl != '') {
                        return toUrl(playUrl);
                    } else {
                        return 'toast://解析失败。'
                    }
                }

            }

        } else {
            return Sniffing(input);
        }
    } catch (e) {
       // setError(e);
        return 'toast://嗅探异常，可能链接无法访问。'
    }
});

