                        } else if (urls[i].indexOf('wkfile') != -1) {
                            return urls[i] + ';{Referer@https://fantuan.wkfile.com/}';
                        } else {
                            return urls[i] + '#isVideo=true#'
                        }
                    }
                } //end for i
            }
        }
    }, srcurl);
    return video
}
//结束x5rule强力嗅探
