    desc: desc,
    title: "",
    extra: {
        js: $.toString(() => {
            function getimgs() {
                var videoElements = document.getElementsByTagName("img");
                if (videoElements == null) {
                    return [];
                }
                let urls = [];
                for (let i = 0; i < videoElements.length; i++) {
                    if (videoElements[i].width > 70 && videoElements[i].height > 70) {
                        const src = videoElements[i].src;
                        if (src == null || src.length < 5 || urls.includes(src) || src.indexOf("data:") == 0) {
                            continue;
                        }
                        //log(src);
                        urls.push(new URL(src).toString());
                    }
                }
                return urls;
            }

            function check() {
                try {
                    let urls = getimgs();
                    //fba.log("getimgs：" + urls.length);
                    if (window.checkedUrls == null) {
                        window.checkedUrls = [];
                    }
                    let checkedUrls = window.checkedUrls;
                    urls = urls.filter(it => {
                        return !checkedUrls.includes(it)
                    });

                    if (urls.length > 0) {
                        let pics = urls.filter(it => it != null && it != "")
                            .map(it => {
                                let u = fba.getHeaderUrl(it);
                                let uu = u.split(";");
                                let pic = u;
                                if (uu.length > 1) {
                                    let u1 = uu[0];
                                    let u2 = uu[1].slice(1, -1).split("&&");
                                    let referer = null;
                                    let ua = null;
                                    let cookie0 = null;
                                    for (let ii of u2) {
                                        let it2 = ii.split("@");
                                        if (it2[0] == "Referer") {
                                            referer = it2[1].replace(/；；/g, ";");
                                        }
                                        if (it2[0] == "User-Agent") {
                                            ua = it2[1].replace(/；；/g, ";");
                                        }
                                        if (it2[0] == "Cookie") {
                                            cookie0 = it2[1].replace(/；；/g, ";");
                                        }
                                    }
                                    pic = u1;
                                    if (referer) {
                                        pic = pic + "@Referer=" + referer;
                                    }
                                    if (ua) {
                                        pic = pic + "@User-Agent=" + ua;
                                    }
                                    if (cookie0) {
                                        pic = pic + "@Cookie=" + cookie0
                                    }

                                } else {
                                    pic = it;
                                }
                                return {
                                    pic: pic,
                                    url: u
                                }
                            });
                        fba.log(pics.length + "");
                        fba.parseLazyRuleAsync($$$().lazyRule((urls) => {
                            //log(urls);

                            let arr = urls.map(it => {
                                return {
                                    title: "",
                                    url: it.pic,
                                    pic_url: it.pic,
                                    col_type: "card_pic_3",
                                    extra: {
                                        cls: "pics"
                                    }
                                }
                            });
                            addItemBefore("pic_line", arr);
                        }, pics), function() {

                        });
                        for (let it of urls) {
                            window.checkedUrls.push(it);
                        }
                    }

                } catch (e) {
                    fba.log(e.toString());
                }
                setTimeout(check, 300);
            }
            check();
        })
    }
});


