        //setError(e);
        showLoading("æ­£åœ¨è¿›è¡Œæ£€ç´¢ï¼Œè¯·ç¨å€™...");
        var video = 'x5Rule://' + input + '@' + $.toString(() => {
            //fba.log(fba.getUrls())
